
define('text!components/LogoutButton/_logoutButton.html',[],function () { return '<div class="eaContainer-LogoutButton">\n    <a class="eaContainer-LogoutButton-link" href="/logout" >Log Out</a>\n</div>\n';});

define('styles!components/LogoutButton/_logoutButton.less',[],function () { return '.eaContainer-LogoutButton {\n  display: inline-block;\n  line-height: 40px;\n  vertical-align: middle;\n}\n.eaContainer-LogoutButton-link {\n  color: #0066B3;\n  font-size: 1.2rem;\n  padding: 0 18px 0 40px;\n  background-color: transparent;\n  background-repeat: no-repeat;\n  background-position: 18px center;\n  border-left: solid 1px #D2D2D2;\n  display: block;\n  text-decoration: none;\n  cursor: pointer;\n  background-image: url("node_modules/container/resources/components/LogoutButton/logout.svg");\n}\n.eaContainer-LogoutButton-link:hover {\n  text-decoration: underline;\n}\n.eaContainer-LogoutButton:hover {\n  background: #e8e8e8;\n  background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #f2f2f2), color-stop(1, #e8e8e8));\n  background: -ms-linear-gradient(top, #f2f2f2, #e8e8e8);\n  background: -o-linear-gradient(center top, #f2f2f2 0%, #e8e8e8 100%);\n  background: -webkit-linear-gradient(center top, #f2f2f2 0%, #e8e8e8 100%);\n  background: -moz-linear-gradient(center top, #f2f2f2 0%, #e8e8e8 100%);\n  background: linear-gradient(center top, #f2f2f2 0%, #e8e8e8 100%);\n}\n@media screen and (max-width: 640px) {\n  .eaContainer-LogoutButton-link {\n    padding-left: 34px;\n    text-indent: -9999px;\n    width: 0;\n  }\n}\n';});

/*global define*/
define('components/LogoutButton/LogoutButtonView',[
    'jscore/core',
    'text!./_logoutButton.html',
    'styles!./_logoutButton.less'
], function (core, template, styles) {
    

    return core.View.extend({

        getTemplate: function () {
            return template;
        },
        getStyle: function () {
            return styles;
        },
        getLink: function () {
            return this.getElement().find(".eaContainer-LogoutButton-link");
        }

    });

});

/*global define*/
define('components/LogoutButton/LogoutButton',[
    'jscore/core',
    './LogoutButtonView'
], function (core, View) {
    

    return core.Region.extend({
        name:"Logout",
        order:"1000",
        position:"right",
        View: View,

        onStart: function (options) {
            this.options = options || {};
            this.addHandler();
        },

        addHandler: function () {
            var button = this.view.getLink();
            button.addEventHandler('click', function (e) {
                e.preventDefault();
                var dialog = confirm('Are you sure you want to log out?');
                if (dialog === true) {
                    window.location.href = button.getAttribute('href');
                }
            });
        }
    });
});
